source.init-db:
create schema source;
SET search_path = source, pg_catalog;

create table r1 (
  a varchar(50),
  b varchar(50)
) with oids;
create table r2 (
  a varchar(50),
  b varchar(50)
) with oids;

insert into r1 values ('1', '2');
insert into r1 values ('1', '3');
insert into r1 values ('1', 'a');

target.init-db:
create schema target;
SET search_path = target, pg_catalog;

create table s (
  a varchar(50),
  b varchar(50)
) with oids;

create table t (
  a varchar(50),
  b varchar(50)
) with oids;

create table w (
  a varchar(50),
  b varchar(50)
) with oids;
