source.init-db:
create schema source;
SET search_path = source, pg_catalog;

create table ibdbookset (
  title varchar(50)
) with oids;

create table locset (
  title varchar(50),
  publisher varchar(50)
) with oids;

create table iblbookset (
  title varchar(50),
  pubid varchar(50)
) with oids;

create table iblpublisherset (
  id varchar(50),
  name varchar(50)
) with oids;

insert into ibdbookset values ('The Hobbit');
insert into ibdbookset values ('The Da Vinci Code');
insert into ibdbookset values ('The Lord of the Rings');

insert into locset values ('The Lord of the Rings', 'Houghton');
insert into locset values ('The Catcher in the Rye', 'Lb Books');

insert into iblbookset values ('The Hobbit', '245');
insert into iblbookset values ('The Catcher in the Rye', '901');

insert into iblpublisherset values ('245', 'Ballantine');
insert into iblpublisherset values ('901', 'Lb Books');

target.init-db:
create schema target;
SET search_path = target, pg_catalog;

create table bookset (
    title varchar(255),
    pubid varchar(255)
) with oids;

create table publisherset (
    id varchar(255),
    name varchar(255)
) with oids;
