source.init-db:
create schema source;
SET search_path = source, pg_catalog;
create table s(
    oid serial,
    a text,
    b text,
    c text
);
INSERT INTO source.s(a, b, c) VALUES ('a', 'b', 'c');

target.init-db:
create schema target;
SET search_path = target, pg_catalog;
create table t3(
    oid serial,
    a text,
    b text,
    c text
);
create table t2(
    oid serial,
    a text,
    b text
);
create table t1(
    oid serial,
    a text,
    b text,
    c text
);
