target.init-db:
create schema target;
SET search_path = target, pg_catalog;

create table person (
  oid serial,
  ssn varchar(50),
  surname varchar(50),
  name varchar(50),
  boss varchar(50)
);
create table cars (
  oid serial,
  plate varchar(50),
  model varchar(50),
  owner varchar(50)
);

insert into person(ssn, surname, name, boss) values ('123', 'Bruce', 'Wayne', 'Boss1');
insert into person(ssn, surname, name, boss) values ('321', 'Bruce', 'Wayne', 'Boss2');
insert into person(ssn, surname, name, boss) values ('231', 'Bruce', 'Wayne', 'Boss4');
insert into cars(plate, model, owner) values ('BM321XS', 'BatMobile', '123');
