target.init-db:
create schema target;
SET search_path = target, pg_catalog;

create table person (
  oid serial,
  ssn varchar(50),
  surname varchar(50),
  name varchar(50),
  boss varchar(50)
);
create table cars (
  oid serial,
  plate varchar(50),
  model varchar(50),
  owner varchar(50)
);

