target.init-db:
create schema target;
SET search_path = target, pg_catalog;

create table r (
  oid serial,
  a varchar(50),
  b varchar(50)
);

create table s (
  oid serial,
  a varchar(50),
  b varchar(50)
);

insert into r(a,b) values ('1', '3');
insert into r(a,b) values ('2', '3');
insert into r(a,b) values ('5', '5');
insert into s(a,b) values ('3', '4');
insert into s(a,b) values ('2', '4');
